import React, {useEffect, useState} from 'react'
import {over} from 'stompjs';
import SockJS from 'sockjs-client';
import './chat.css';

var stompClient =null;
const ChatWindow = () => {
    const [privateChats, setPrivateChats] = useState(new Map());
    const [publicChats, setPublicChats] = useState([]);
    const [tab,setTab] =useState("CHATROOM");
    const [userData, setUserData] = useState({
        username: '',
        receivername: '',
        connected: false,
        message: ''
    });
    let [clients, admins] = [];
    let role;
    let headers = {
        Authorization:  localStorage.getItem('token'),
    };

    useEffect(() => {
        handleUsername();
    }, []);

    const connect =()=>{
        let Sock = new SockJS('http://localhost:8083/ws');
        stompClient = over(Sock);
        stompClient.connect({},onConnected, onError);
    }

    const onConnected = () => {
        setUserData({...userData,"connected": true});
        stompClient.subscribe('/chatroom/public', onMessageReceived);
        userJoin();
        if(role === 'ADMIN' && clients.includes(userData.username))
        {
            stompClient.subscribe('/user/'+userData.username+'/private', onPrivateMessage);
        }

        if(role === 'CLIENT' && admins.includes(userData.username))
        {
            stompClient.subscribe('/user/'+userData.username+'/private', onPrivateMessage);
        }
    }
    const fetchUsers = async () => {
        try {
            const token = localStorage.getItem('token');
             role = localStorage.getItem('role');
            let response;
            if(role === 'ADMIN'){
                 response = await fetch('http://localhost:8080/user/chat-clients', {
                    method: 'GET',
                    headers: {
                        'Authorization': 'Bearer ' + token,
                    },
                });
                 clients = response.body;
            }
            else {
                 response = await fetch('http://localhost:8080/user/chat-admins', {
                    method: 'GET',
                    headers: {
                        'Authorization': 'Bearer ' + token,
                    },
                });
                  admins = response.body;
            }


            if (!response.ok) {
                console.error('Failed to fetch users for chat');
                return [];
            }

            return await response.json();
        } catch (error) {
            console.error('Error fetching  users:', error);
            return [];
        }
    };

    const userJoin= async ()=>{
            const usersList = await fetchUsers();
            usersList.forEach((currentUser, index) => {
                console.log(`Current user ${index + 1}:`, currentUser);
                const chatMessage = {
                    senderName: currentUser,
                    status: "JOIN"
                };
                stompClient.send("/app/message", headers, JSON.stringify(chatMessage));
            });

    }


    const onMessageReceived = (payload)=>{
        const payloadData = JSON.parse(payload.body);

        switch(payloadData.status){
            case "JOIN":
                if(!privateChats.get(payloadData.senderName)){
                    privateChats.set(payloadData.senderName,[]);
                    setPrivateChats(new Map(privateChats));
                }
                break;
            case "MESSAGE":
                publicChats.push(payloadData);
                setPublicChats([...publicChats]);
                break;
        }
    }

    const onPrivateMessage = (payload)=>{
        const payloadData = JSON.parse(payload.body);
        if(privateChats.get(payloadData.senderName)){
            privateChats.get(payloadData.senderName).push(payloadData);
            setPrivateChats(new Map(privateChats));
        }else{
            let list =[];
            list.push(payloadData);
            privateChats.set(payloadData.senderName,list);
            setPrivateChats(new Map(privateChats));
        }
    }

    const onError = (err) => {
        console.log(err);

    }

    const handleMessage =(event)=>{
        const {value}=event.target;
        setUserData({...userData,"message": value});
    }
    const sendValue=()=>{
        if (stompClient) {
            const chatMessage = {
                senderName: userData.username,
                message: userData.message,
                status: "MESSAGE"
            };
            console.log(chatMessage);
            stompClient.send("/app/message", headers, JSON.stringify(chatMessage));
            setUserData({...userData,"message": ""});
        }
    }

    const sendPrivateValue=()=>{
        if (stompClient) {
            const chatMessage = {
                senderName: userData.username,
                receiverName: tab,
                message: userData.message,
                status: "MESSAGE"
            };

            if(userData.username !== tab){
                privateChats.get(tab).push(chatMessage);
                setPrivateChats(new Map(privateChats));
            }
            stompClient.send("/app/private-message", headers, JSON.stringify(chatMessage));
            setUserData({...userData,"message": ""});
        }
    }
    const getLoggedInUsername = async () => {
        try {
            const response = await fetch('http://localhost:8080/auth/username', {
                method: 'GET',
                headers: {
                    'Authorization': 'Bearer ' + localStorage.getItem('token'),
                },
            });

            if (!response.ok) {
                throw new Error('Network response was not ok.');
            }

            const username = await response.text();
            console.info("Logged-in Username:" + username);
            return username;
        } catch (error) {
            console.error('Error fetching logged-in username:', error);
            throw error;
        }
    };

    const handleUsername=(event)=>{
        getLoggedInUsername().then((loggedUser) => setUserData({...userData,"username": loggedUser}));;
    }

    const registerUser=()=>{
        connect();
    }




    return (
        <div className="container">
            {userData.connected?
                <div className="chat-box">
                    <div className="member-list">
                        <ul>
                            <li onClick={()=>{setTab("CHATROOM")}} className={`member ${tab==="CHATROOM" && "active"}`}>Chatroom</li>
                            {[...privateChats.keys()].map((name,index)=>(
                                <li onClick={()=>{setTab(name)}} className={`member ${tab===name && "active"}`} key={index}>{name}</li>
                            ))}
                        </ul>
                    </div>
                    {tab==="CHATROOM" && <div className="chat-content">
                        <ul className="chat-messages">
                            {publicChats.map((chat,index)=>(
                                <li className={`message ${chat.senderName === userData.username && "self"}`} key={index}>
                                    {chat.senderName !== userData.username && <div className="avatar">{chat.senderName}</div>}
                                    <div className="message-data">{chat.message}</div>
                                    {chat.senderName === userData.username && <div className="avatar self">{chat.senderName}</div>}
                                </li>
                            ))}
                        </ul>

                        <div className="send-message">
                            <input type="text" className="input-message" placeholder="enter the message" value={userData.message} onChange={handleMessage} />
                            <button type="button" className="send-button" onClick={sendValue}>send</button>
                        </div>
                    </div>}
                    {tab!=="CHATROOM" && <div className="chat-content">
                        <ul className="chat-messages">
                            {[...privateChats.get(tab)].map((chat,index)=>(
                                <li className={`message ${chat.senderName === userData.username && "self"}`} key={index}>
                                    {chat.senderName !== userData.username && <div className="avatar">{chat.senderName}</div>}
                                    <div className="message-data">{chat.message}</div>
                                    {chat.senderName === userData.username && <div className="avatar self">{chat.senderName}</div>}
                                </li>
                            ))}
                        </ul>

                        <div className="send-message">
                            <input type="text" className="input-message" placeholder="enter the message" value={userData.message} onChange={handleMessage} />
                            <button type="button" className="send-button" onClick={sendPrivateValue}>send</button>
                        </div>
                    </div>}
                </div>
                :
                <div className="register">
                    <p style={{ color: 'white', fontSize: '20px', fontWeight: 'bold', textAlign: 'center' , marginRight: '20px', marginTop: '20px'}}>
                        You are logged in as: {userData.username}
                    </p>
                    <button type="button" onClick={registerUser}>
                    Connect to chat
                    </button>
                </div>}
        </div>
    )
}

export default ChatWindow
