import React, { useState, useEffect, useRef } from 'react';
import { MessageBox, MessageList, Input, Button, ChatItem, ChatList } from 'react-chat-elements';
import SockJsClient from 'react-stomp';

const ChatWindow = ({ isOpen, onClose }) => {
    const [message, setMessage] = useState('');
    const [selectedUser, setSelectedUser] = useState('');
    const [users, setUsers] = useState([]);
    const [messages, setMessages] = useState([]);
    const clientRef = useRef(null);

    const handleMessage = (msg) => {
        setMessages([...messages, msg]);
    };

    const fetchAdminUsers = async () => {
        try {
            const token = localStorage.getItem('token');
            const response = await fetch('http://localhost:8080/user/chat-admins', {
                method: 'GET',
                headers: {
                    'Authorization': 'Bearer ' + token,
                },
            });

            if (!response.ok) {
                console.error('Failed to fetch admin users for chat');
                return [];
            }

            const data = await response.json();
            return data;
        } catch (error) {
            console.error('Error fetching admin users:', error);
            return [];
        }
    };

    useEffect(() => {
        fetchAdminUsers().then((usersData) => {
            setUsers(usersData);
        });
    }, []);

    const handleSendMessage = () => {
        if (selectedUser && message.trim() !== '' && clientRef.current && clientRef.current.sendMessage) {
            const newMessage = {
                text: message,
                sender: 'CurrentLoggedInUser', // Replace with the logged-in user
                messageType: 'MESSAGE', // Adjust message type if needed based on the MessageType enum
            };
            clientRef.current.sendMessage(`/app/chat/${selectedUser}`, JSON.stringify(newMessage));
            setMessage('');
        }
    };

    const handleKeyPress = (event) => {
        if (event.key === 'Enter') {
            handleSendMessage();
        }
    };

    const handleConnect = () => {
        console.log('Connected to WebSocket');
    };

    const handleDisconnect = () => {
        console.log('Disconnected from WebSocket');
    };

    const handleError = (error) => {
        console.error('WebSocket error:', error);
    };

    return (
            <div className="chat-window">
                <ChatList
                    className="chat-list"
                    dataSource={messages.map((msg, index) => (
                        <ChatItem
                            key={index}
                            avatar={'Avatar URL'} // Replace with sender's avatar URL
                            alt={'User'}
                            title={msg.sender}
                            subtitle={msg.text}
                            date={new Date()}
                            unread={0}
                        />
                    ))}
                />
                <Input
                    placeholder="Type a message..."
                    multiline={false}
                    value={message}
                    onChange={(e) => setMessage(e.target.value)}
                    onKeyPress={handleKeyPress}
                    rightButtons={
                        <Button
                            color="white"
                            backgroundColor="black"
                            text="Send"
                            onClick={handleSendMessage}
                        />
                    }
                />
                <SockJsClient
                    url="http://localhost:8083/chat" // Replace with your backend URL
                    topics={[`/topic/messages/${selectedUser}`]} // Use selected user for topic
                    onMessage={handleMessage}
                    onConnect={handleConnect}
                    onDisconnect={handleDisconnect}
                    onError={handleError}
                    debug={false}
                    ref={(client) => (clientRef.current = client)}
                />
            </div>
    );
};

export default ChatWindow;
