import React, { useState, useEffect, useRef } from 'react';
import SockJsClient from 'react-stomp';

const ChatWindow = ({ isOpen, onClose }) => {
    const [message, setMessage] = useState('');
    const [selectedUser, setSelectedUser] = useState('');
    const [users, setUsers] = useState([]);
    const [messages, setMessages] = useState([]);
    const clientRef = useRef(null);

    const handleMessage = (msg) => {
        setMessages([...messages, msg]);
    };

    const fetchAdminUsers = async () => {
        try {
            const token = localStorage.getItem('token');
            const response = await fetch('http://localhost:8080/user/chat-admins', {
                method: 'GET',
                headers: {
                    'Authorization': 'Bearer ' + token,
                },
            });

            if (!response.ok) {
                console.error('Failed to fetch admin users for chat');
                return [];
            }

            const data = await response.json();
            return data;
        } catch (error) {
            console.error('Error fetching admin users:', error);
            return [];
        }
    };

    useEffect(() => {
        fetchAdminUsers().then((usersData) => {
            setUsers(usersData);
        });
    }, []);

    const handleSendMessage = () => {
        if (selectedUser && message.trim() !== '' && clientRef.current && clientRef.current.sendMessage) {
            clientRef.current.sendMessage(`/app/chat/${selectedUser}`, {
                text: message,
                sender: 'CurrentLoggedInUser', // Replace with the logged-in user
                messageType: 'TEXT', // Adjust message type if needed
            });
            setMessage('');
        }
    };

    const handleKeyPress = (event) => {
        if (event.key === 'Enter') {
            handleSendMessage();
        }
    };

    return (
        isOpen && (
            <div style={{ display: 'flex', flexDirection: 'column', height: '100%', padding: '20px' }}>
                <div style={{ flex: '1', overflowY: 'scroll' }}>
                    {messages.map((msg, index) => (
                        <div key={index} style={{ textAlign: msg.sender === 'CurrentLoggedInUser' ? 'right' : 'left' }}>
                            <p style={{ margin: '5px', backgroundColor: msg.sender === 'CurrentLoggedInUser' ? '#DCF8C6' : '#ECE5DD', padding: '8px', borderRadius: '8px', display: 'inline-block' }}>
                                {msg.text}
                            </p>
                        </div>
                    ))}
                </div>
                <div style={{ display: 'flex', alignItems: 'center', marginTop: '20px' }}>
                    <select value={selectedUser} onChange={(e) => setSelectedUser(e.target.value)}>
                        <option value="">Select a user</option>
                        {users.map((user) => (
                            <option key={user.id} value={user.username}>
                                {user.username}
                            </option>
                        ))}
                    </select>
                    <input
                        type="text"
                        value={message}
                        onChange={(e) => setMessage(e.target.value)}
                        onKeyPress={handleKeyPress}
                        placeholder="Type a message..."
                        style={{ marginLeft: '10px', flex: '1' }}
                    />
                    <button onClick={handleSendMessage} style={{ marginLeft: '10px' }}>Send</button>
                </div>
                <SockJsClient
                    url='http://localhost:your_backend_port/chat' // Replace with your backend URL
                    topics={[`/topic/messages/${selectedUser}`]} // Use selected user for topic
                    onMessage={handleMessage}
                    debug={false}
                    ref={(client) => (clientRef.current = client)}
                />
            </div>
        )
    );
};

export default ChatWindow;
