import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import Chart from 'chart.js/auto';

const EnergyConsumptionPage = () => {
    const [selectedDate, setSelectedDate] = useState(new Date());
    const [devices, setDevices] = useState([]);
    const [selectedDevice, setSelectedDevice] = useState(null);
    const [energyData, setEnergyData] = useState([]);
    const chartRef = useRef(null);

    useEffect(() => {
        fetchDevices();
    }, []);

    const fetchDevices = async () => {
        try {
            const response = await axios.get('http://localhost:8081/user/devices', {
                headers: {
                    Authorization: localStorage.getItem('token'),
                },
            });
            setDevices(response.data);
        } catch (error) {
            console.error('Error fetching devices:', error);
        }
    };

    const handleDeviceChange = (deviceId) => {
        setSelectedDevice(deviceId);
        console.log('Selected Device ID:', deviceId);
    };

    const handleDateChange = (e) => {
        const selectedDateTime = new Date(e.target.value);
        selectedDateTime.setMinutes(0);
        selectedDateTime.setSeconds(0);

        setSelectedDate(selectedDateTime);
    };

    const fetchData = async () => {
        try {
            const formattedDate = new Date(selectedDate);
            formattedDate.setHours(23, 59, 59); // Set hours, minutes, and seconds to 23:59:59

            const year = formattedDate.getFullYear();
            const month = ('0' + (formattedDate.getMonth() + 1)).slice(-2); // Months are zero-based
            const day = ('0' + formattedDate.getDate()).slice(-2);
            const hours = ('0' + formattedDate.getHours()).slice(-2);
            const minutes = ('0' + formattedDate.getMinutes()).slice(-2);
            const seconds = ('0' + formattedDate.getSeconds()).slice(-2);

            const formattedEndTime = `${year}-${month}-${day}T${hours}:${minutes}:${seconds}`;

            const formattedDateTime = `${year}-${month}-${day}T00:00:00`; // Start time remains the same

            const response = await axios.get('http://localhost:8281/history/energyconsumption', {
                params: {
                    deviceId: selectedDevice,
                    dateTime: formattedDateTime,
                    endTime: formattedEndTime,
                },
            });
            setEnergyData(response.data);
        } catch (error) {
            console.error('Error fetching energy consumption data:', error);
        }
    };

    useEffect(() => {
        if (selectedDevice && selectedDate) {
            fetchData();
        }
    }, [selectedDevice, selectedDate]);

    useEffect(() => {
        if (energyData.length > 0) {
            renderChart();
        }
    }, [energyData]);

    useEffect(() => {
        if (chartRef.current !== null) {
            chartRef.current.destroy();
            renderChart();
        }
    }, [selectedDate]);

    const renderChart = () => {
        const labels = energyData.map((data) => data.dateTime);
        const values = energyData.map((data) => data.consumption);

        const ctx = document.getElementById('energyChart').getContext('2d');

        chartRef.current = new Chart(ctx, {
            type: 'line',
            data: {
                labels: labels,
                datasets: [
                    {
                        label: 'Energy Consumption',
                        data: values,
                        fill: false,
                        borderColor: 'rgb(75, 192, 192)',
                        tension: 0.1,
                    },
                ],
            },
            options: {
                scales: {
                    x: {
                        title: {
                            display: true,
                            text: 'Date/Time',
                            color: 'white',
                        },
                        ticks: {
                            color: 'white',
                        },
                    },
                    y: {
                        title: {
                            display: true,
                            text: 'Energy Consumption',
                            color: 'white',
                        },
                        ticks: {
                            color: 'white',
                        },
                    },
                },
            },
        });
    };

    return (
        <div>
            <h1 style={{ color: 'white' }}>Energy Consumption</h1>
            <div>
                <label htmlFor="calendar" style={{ color: 'white' }}>Select Date:</label>
                <input
                    type="datetime-local"
                    id="calendar"
                    value={selectedDate.toISOString().slice(0, -8)} // Truncate to remove minutes and seconds
                    onChange={handleDateChange}
                />
            </div>
            <div>
                <label htmlFor="deviceDropdown" style={{ color: 'white' }}>Select Device:</label>
                <select id="deviceDropdown" onChange={(e) => handleDeviceChange(e.target.value)}>
                    <option value="">Select</option>
                    {Array.isArray(devices) && devices.length > 0 && devices.map((device) => (
                        <option key={device.id} value={device.id}>
                            {device.id}
                        </option>
                    ))}
                </select>
            </div>
            <div>
                <h3 style={{ color: 'white' }}>Energy Consumption Chart</h3>
                <canvas id="energyChart" width="800" height="400"></canvas>
            </div>
        </div>
    );
};

export default EnergyConsumptionPage;
