import React, { useState, useEffect } from 'react';

const EnergyConsumptionPage = () => {
    const [devices, setDevices] = useState([]);
    const [selectedDevice, setSelectedDevice] = useState('');

    useEffect(() => {
        fetchDeviceIds();
    }, []);

    const fetchDeviceIds = async () => {
        try {
            const response = await fetch('http://localhost:8081/devices', {
                headers: {
                    'Authorization': localStorage.getItem('token'),
                },
            });

            if (response.ok) {
                const devicesData = await response.json();
                console.log('Fetched Devices Data:', devicesData); // Log fetched data to inspect its structure

                if (Array.isArray(devicesData)) {
                    setDevices(devicesData);
                } else {
                    throw new Error('Devices data is not an array');
                }
            } else {
                throw new Error('Failed to fetch devices');
            }
        } catch (error) {
            console.error('Error fetching devices:', error);
        }
    };

    const handleDeviceChange = (event) => {
        setSelectedDevice(event.target.value);
    };

    return (
        <div>
            <label htmlFor="deviceDropdown">Select Device:</label>
            <select id="deviceDropdown" onChange={handleDeviceChange} value={selectedDevice}>
                <option value="">Select a device</option>
                {devices.map((device) => (
                    <option key={device.id} value={device.id}>
                        {device.id}
                    </option>
                ))}
            </select>
            <p>Selected Device: {selectedDevice}</p>
        </div>
    );
};

export default EnergyConsumptionPage;
