import React, { useState, useEffect } from 'react';
import axios from 'axios';

const EnergyConsumptionPage = () => {
    const [selectedDate, setSelectedDate] = useState(new Date());
    const [devices, setDevices] = useState([]);
    const [selectedDevice, setSelectedDevice] = useState(null);

    useEffect(() => {
        // Fetch devices from the backend API on component mount
        fetchDevices();
    }, []);

    const fetchDevices = async () => {
        try {
            const response = await axios.get('http://localhost:8081/devices', {
                headers: {
                    Authorization: 'Bearer ' + localStorage.getItem('token'),
                },
            });
            setDevices(response.data);
        } catch (error) {
            console.error('Error fetching devices:', error);
        }
    };

    const handleDeviceChange = (deviceId) => {
        setSelectedDevice(deviceId);
    };

    const handleDateChange = (e) => {
        setSelectedDate(new Date(e.target.value));
    };

    const fetchData = async () => {
        try {
            const response = await axios.get('http://localhost:8281/energyconsumption', {
                params: {
                    deviceId: selectedDevice,
                    dateTime: selectedDate.toISOString(),
                },
            });
            // Process the response data here and integrate with your chart library
            console.log('Energy Consumption Data:', response.data);
        } catch (error) {
            console.error('Error fetching energy consumption data:', error);
        }
    };
    useEffect(() => {
        if (selectedDevice && selectedDate) {
            fetchData();
        }
    }, [selectedDevice, selectedDate]);

    return (
        <div>
            <h1>Energy Consumption</h1>
            <div>
                <label htmlFor="calendar">Select Date:</label>
                <input
                    type="date"
                    id="calendar"
                    value={selectedDate.toISOString().slice(0, 10)}
                    onChange={handleDateChange}
                />
            </div>
            <div>
                <label htmlFor="deviceDropdown">Select Device:</label>
                <select id="deviceDropdown" onChange={(e) => handleDeviceChange(e.target.value)}>
                    <option value="">Select</option>
                    {devices.map((device) => (
                        <option key={device.id} value={device.id}>
                            {device.name} - {device.id}
                        </option>
                    ))}
                </select>
            </div>
            <div>
                {/* Placeholder for Line Chart component */}
                <h3>Line Chart Placeholder</h3>
                {/* You can integrate an actual line chart component here */}
            </div>
        </div>
    );
};

export default EnergyConsumptionPage;
